import React from 'react';
import { View, StyleSheet } from 'react-native';
import Arrow from './Arrow';

const Board = ({ currentArrow, arrows, windowDimensions }) => {
  const boardSize = Math.min(windowDimensions.width * 0.8, windowDimensions.height * 0.6);

  return (
    <View style={[styles.board, { width: boardSize, height: boardSize }]}>
      {[...Array(4)].map((_, rowIndex) => (
        <View key={rowIndex} style={styles.row}>
          {[...Array(4)].map((_, colIndex) => {
            const index = rowIndex * 4 + colIndex;
            const arrowProps = arrows
              ? {
                  direction: arrows[index].direction,
                  color: arrows[index].color,
                }
              : currentArrow && index === currentArrow.position
              ? {
                  direction: currentArrow.direction,
                  color: currentArrow.color,
                }
              : {
                  direction: null,
                  color: '#E0E0E0',
                };

            return (
              <View key={colIndex} style={styles.cell}>
                <Arrow {...arrowProps} />
              </View>
            );
          })}
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  board: {
    aspectRatio: 1,
    backgroundColor: '#FFFFFF',
    borderRadius: 10,
    padding: 5,
    elevation: 5,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
  },
  row: {
    flex: 1,
    flexDirection: 'row',
  },
  cell: {
    flex: 1,
    aspectRatio: 1,
    margin: 2,
    backgroundColor: '#F0F0F0',
    borderRadius: 5,
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default Board;
